package com.my.util;import com.my.vo.Request;import com.my.vo.Requests;import javax.xml.bind.JAXBContext;import javax.xml.bind.JAXBException;import javax.xml.bind.Marshaller;import java.io.ByteArrayInputStream;import java.io.InputStream;import java.io.StringWriter;import java.io.UnsupportedEncodingException;public class XmlUtils {    public static <T> T toObj(Class<T> clazz, String xml) throws JAXBException, UnsupportedEncodingException {        JAXBContext context = JAXBContext                .newInstance(new Class[] { clazz });        InputStream buf = new ByteArrayInputStream(xml.getBytes("UTF-8"));        return (T) context.createUnmarshaller().unmarshal(buf);    }    public static <T> String toXml(T object) throws JAXBException {        StringWriter write = new StringWriter();        JAXBContext context = JAXBContext.newInstance(object.getClass());        context.createMarshaller().marshal(object, write);        Marshaller marshaller = context.createMarshaller();        marshaller.setProperty(Marshaller.JAXB_ENCODING, "UTF-8");        marshaller.setProperty(Marshaller.JAXB_FORMATTED_OUTPUT, Boolean.TRUE);        marshaller.setProperty(Marshaller.JAXB_FRAGMENT, Boolean.TRUE);        return write.getBuffer().toString();    }}